schema:
{
          "type": "object",
          "properties": {
            "full_name": {
              "type": "string",
              "title": "What is your full name?"
            },
            "date_of_birth": {
              "type": "string",
              "format": "date",
              "title": "What is your date of birth?"
            },
            "income_source": {
              "type": "string",
              "title": "What is your primary source of income?",
              "enum": [
                "Employment",
                "Business ownership",
                "Investments",
                "Other"
              ]
            },
            "account_purpose": {
              "type": "string",
              "title": "What is the purpose of opening this account?",
              "enum": [
                "Personal savings",
                "Checking for daily transactions",
                "Investment opportunities",
                "Other"
              ]
            },
            "transaction_frequency": {
              "type": "string",
              "title": "How often do you expect to make transactions on this account?",
              "enum": [
                "Daily",
                "Weekly",
                "Monthly",
                "Rarely"
              ]
            },
            "average_balance": {
              "type": "string",
              "title": "What is your estimated average account balance?",
              "enum": [
                "<$1,000",
                "$1,000 - $5,000",
                "$5,000 - $10,000",
                ">$10,000"
              ]
            },
            "additional_services": {
              "type": "string",
              "title": "Are you interested in additional banking services?",
              "enum": [
                "Credit cards",
                "Loans",
                "Investment advisory",
                "Other"
              ]
            },
            "account_management_preference": {
              "type": "string",
              "title": "How do you prefer to manage your account?",
              "enum": [
                "Online banking",
                "In-person visits",
                "Mobile app",
                "Other"
              ]
            },
            "specific_requirements": {
              "type": "string",
              "title": "Do you have any specific requirements or preferences for this account?",
              "enum": [
                "Interest-bearing",
                "No monthly fees",
                "Access to ATMs",
                "Other"
              ]
            },
            "banking_familiarity": {
              "type": "string",
              "title": "What is your familiarity with banking products and services?",
              "enum": [
                "Basic",
                "Intermediate",
                "Advanced",
                "Limited"
              ]
            }
          },
          "required": [
            "full_name",
            "date_of_birth",
            "income_source",
            "account_purpose",
            "transaction_frequency",
            "average_balance",
            "account_management_preference",
            "banking_familiarity"
          ]
        }
